<script setup lang="ts"></script>
<style lang="scss">
.connect-leave-message-wrapper {
  display: flex;
  justify-content: flex-end;
  margin-right: var(--space-xl);
  height: 20vh;
  @media screen and (max-width: 1279px) {
    margin-right: var(--space-xxl);
  }
  @media screen and (max-width: 560px) {
    height: 0;
  }
  a {
    position: absolute;
    right: var(--space-xl);
    width: calc((100% - (var(--space-xl) * 2)) * 0.35);
    cursor: pointer;
    @media screen and (max-width: 1279px) {
      right: var(--space-xxl);
      width: calc((100% - (var(--space-xxl) * 2)) * 0.35);
    }
    @media screen and (max-width: 560px) {
      width: 100%;
    }
    #leaveMeAMessageDesktop {
      position: relative;
      width: 100%;
      height: unset;
    }
  }
  @media screen and (max-width: 560px) {
    #leaveMeAMessageDesktop {
      display: none;
    }
  }
}
.connect-leave-message-wrapper.mobile {
  display: none;
  @media screen and (max-width: 560px) {
    display: block;
  }
  #leaveMeAMessageMobile {
    position: relative;
    width: 100%;
    height: unset;
  }
  .leave-message-divider {
    width: 100%;
    position: absolute;
    right: var(--space-xl);
    z-index: 0;
    @media screen and (max-width: 1279px) {
      right: var(--space-xxl);
    }
  }
}
</style>
